//Declare a function named greet that takes a string parameter name and returns a greeting message
var firstGreeting = function (message) {
    return "Hi ".concat(message);
};
var products = [
    {
        id: 123,
        name: "Heaphone"
    },
    {
        id: 123456,
        name: "Iphone"
    },
    {
        id: 1234,
        name: "Car"
    }
];
var getProduct = function (id) {
    return products.find(function (product) { return product.id === id; });
};
console.log(getProduct(1234));
